import React, { useContext } from 'react';
import { writers, createSelectionList } from '../../Writers';
import Turn from '../Turn/Turn';
import './QuizForm.css'
import { ColorContext } from '../../App';


const QuizForm = () => {

    const initialState = {
         turnData: createSelectionList(writers),

    }
    const [highLight, setHighLight] = useContext(ColorContext)
    const selectBookHandler = (answer) => {
        let isCorrect = initialState.turnData.checkAnswer(answer);
        setHighLight(isCorrect ? 'correct' : 'incorrect');
        }

   return(
       <div className="quiz_container">
           <header className="quiz_header">
               <h2>Author quiz</h2>
               <p> Select a right author</p>
           </header>
           <section>
                <Turn
                 selectBookHandler={selectBookHandler}
                 books={initialState.turnData.books}
                 author={initialState.turnData.author}
                 highLight={highLight}
                 />
           </section>
       </div>
   )

}

export default QuizForm;
