import React from 'react';
import './Turn.css';


const Turn = ({author, books}) => {

    return(

        <div className="quiz_content">
            <div>
                <img className="author_img" src={author.imageUrl} alt={author.name}></img>
            </div>
        <div>
            <ul>
                {books.map((book, key) => {
                    return <li className="book_list" index={key}> {book} </li>
                })}
            </ul>
        </div>

        </div>
    )

}

export default Turn;
