import React from 'react';
import './Turn.css';
import Book from '../Book/Book';


const Turn = ({author, books, selectBookHandler}) => {

    return(

        <div className="quiz_content">
            <div>
                <img className="author_img" src={author.imageUrl} alt={author.name}></img>
            </div>
        <div className="book_list_container">
            {books.map((title)=> 
            <Book
             title={title}
             key={title}
             selectBookHandler={selectBookHandler}
             highLight={highLight}
            />)}
        </div>

        </div>
    )

}

export default Turn;
