import React, { useRef, useState } from 'react';
import { createSelectionList, writers } from '../../Writers';
import Container from '@material-ui/core/Container';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import './QuizForm.css';
import Turn from '../Turn/Turn';
import { useHistory } from 'react-router';
import './QuizForm.css';
import ProgressBar from '../../UI/ProgressBar/ProgressBar';
import { useEffect } from 'react';

export default function QuizForm() {

    const initialState = {
        writers,
        quizData: createSelectionList(writers),
    }
    const [progress, setProgress] = useState(0);
    const history = useHistory();
    const cardBackground = useRef(0);

    let handler;

    const refresh = () => {
        handler = setTimeout(() => { 
            history.push({ pathname: "/empty" });
            history.goBack();

        }, 2000);   
      };

    useEffect(() => {
        let p = setProgress((prevProgress) => (prevProgress >= 100 ? 10 : prevProgress + 10))         

        return () => handler && p;
      }, [handler]);

    const selectBookHandler =  (title) => {
        let isCorrect = initialState.quizData.checkAnswer(title);

        if(isCorrect ) {
            cardBackground.current.style.backgroundColor = "green"
            refresh()
        }
        else {
            cardBackground.current.style.backgroundColor = "#FF7F50";
            refresh()

        }

    }

    useEffect(() => {
        const getProgress = async () => {
            const parsedCount =  Number(localStorage.getItem("progress"))
            setProgress(parsedCount);
        }
        getProgress();

      }, [])

    useEffect(() => {
        localStorage.setItem("progress", progress)
      }, [progress]);

      const onFinishQuiz = () => {
          localStorage.removeItem('progress');
          setProgress(0)
          history.push({ pathname: "/" });
      }

    return (

        <Container maxWidth="md" className="quiz_container" >
            <ProgressBar progress={progress} />

            <Card className="quiz_card" ref={cardBackground} style={{backgroundColor: "white" }}>
                <CardContent className="quiz_card_content">
                    <header className="quiz_header">
                        <Typography variant="h5" component="h2">
                            Book Quiz
                        </Typography>
                        <Typography variant="subtitle1" color="textSecondary">
                            Select a right book
                        </Typography>
                    </header>
                    <section className="quiz_section"         >
                        <Turn quizData={initialState.quizData} progress={progress} onFinishQuiz={onFinishQuiz}   selectBookHandler={selectBookHandler} />
                    </section>
                </CardContent>
            </Card> 
        </Container>
    );
}
