import React, { useContext } from 'react';
import { ColorContext } from '../../App';

const Book = React.memo(({title, selectBookHandler }) => {

  const highLight = useContext(ColorContext);
  console.log(highLight[0])

  function highlightToBgColor(highLight){
    const mapping = {
      'none': 'green',
      'correct': '#006400',
      'incorrect': 'red'
    };
    return mapping[highLight];
  }

    return(  <div  >
            <ul>
            <li style={{backgroundColor: highlightToBgColor(highLight)}}  onClick={()=> {selectBookHandler(title)}}  className="book_list"> {title} </li>
        </ul>

        </div>

    )

})

export default Book;
