import React, { useRef } from 'react';
import { createSelectionList, writers } from '../../Writers';
import Container from '@material-ui/core/Container';
import Card from '@material-ui/core/Card';

import './QuizForm.css';
import Turn from '../Turn/Turn';
import { useHistory } from 'react-router';

import './QuizForm.css';






export default function QuizForm() {

    const initialState = {
        writers,
        quizData: createSelectionList(writers),
    }
    const history = useHistory();
    //const [answer , setAnswer] = useState()
    const box = useRef(0);

    const selectBookHandler = (title) => {
         let isCorrect = initialState.quizData.checkAnswer(title);
        if(isCorrect) {
            box.current.style.backgroundColor = "green"
            setTimeout(() => {  

                history.push({ pathname: "/empty" });
                history.goBack();
            }, 1000);   
        }
        else {
            box.current.style.backgroundColor = "red"
            return;
        }

    }

    return (

        <Container className="quiz_container">
            <Card>
            <header className="quiz_header">
            <h2>Author quiz</h2>
            <p> Select a right author</p>
        </header>

        <section className="quiz_section"        ref={box} style={{backgroundColor: "white" }} >
            <Turn quizData={initialState.quizData}   selectBookHandler={selectBookHandler} />

        </section>

            </Card>


        </Container>


)

}
