import React from 'react';
import { createSelectionList, writers } from '../../Writers';
import './QuizForm.css';
import Turn from '../Turn/Turn';

export const initialState = {
    writers,
    quizData: createSelectionList(writers),
    answerStatus: 'none'  
  }


  export const selectBookHandler = (bookId) => {
    let isCorrect = initialState.turnData.checkAnswer(bookId);
    return isCorrect ? 'correct' : 'incorrect'
  }

export default function QuizForm() {

    return (
        <div className="quiz_container">
        <header className="quiz_header">
            <h2>Author quiz</h2>
            <p> Select a right author</p>
        </header>
        <section>
            <Turn quizData={initialState.quizData} />

        </section>

    </div>


)

}
