import React from 'react';
import {Provider} from 'react-redux'
import { writers, getTurnData } from './Writers';
import { createStore } from 'redux';

import './App.css';
import QuizForm from './Quiz/QuizForm/QuizForm';

export const initialState = {
  writers,
  turnData: getTurnData(writers),
  answerStatus: 'none'

}

const actionHandlers = {
	'ANSWER_SELECTED': (state, action) => Object.assign({}, state, { answerStatus: selectBookHandler(action.answer, state.turnData) }),
	'CONTINUE': (state, action) => Object.assign({}, state, { answerStatus: 'none', turnData: getTurnData(state.writers) }),
};
const reducer = (state = initialState, action) => {
	let actionHandler = actionHandlers[action.type];

	return (actionHandler && actionHandler(state, action)) || state;
};

const store = createStore(
	reducer,
	window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
);


export const selectBookHandler = (answer) => {
  let isCorrect = initialState.turnData.author.books.includes(answer);
  return isCorrect ? 'correct' : 'incorrect'
}




function App() {



     return (
       <div>
         <Provider store={store}>
           <QuizForm selectBookHandler={selectBookHandler} />
          </Provider>

      </div>
  );
}

export default App;
