import React from 'react';
import './Turn.css';
import Book from '../Book/Book';
import Typography from '@material-ui/core/Typography';




const Turn = ({quizData, selectBookHandler, answerStatus }) => {

    return(
        <React.Fragment>
            {quizData.authors.map(au => {
          return <div className="quiz_content">
            <div className="authorImageDiv">
                <img alt="author_img" src={au.imageUrl}></img>
                <Typography variant="h5" component="h2">
                            {au.name}
                </Typography>
            </div>
             <div className="book_list_container">
                <ul>
                    {au.books.map(book => {
                        return <Book book={book}  onAnswerSelected={selectBookHandler} answerStatus={answerStatus}  /> 
                    })}
                </ul>
            </div>
        </div>
         } )}
        </React.Fragment>
    );

}

export default Turn;
