import React from 'react';
import './Turn.css';
import Book from '../Book/Book';


const Turn = ({quizData, selectBookHandler, answerStatus }) => {


    return(

        <div className="quiz_content">
            <div className="authorImage">
                <img alt="author_img" src={quizData.author.imageUrl}></img>
            </div>
        <div className="book_list_container">
            <ul>
                {quizData && quizData.books.map(book => {
                    return <Book book={book} onAnswerSelected={selectBookHandler} answerStatus={answerStatus}  /> 

                })}
            </ul>


        </div>

        </div>
    )

}

export default Turn;
