import React, { useState } from 'react';

const initialHighlight = {
    neutral: "#000000",
    correct: " #009933",
    incorrect: "#ff3300"
}

const HighlightContext = React.createContext({initialHighlight: {}})

export default props => {
    const [highlight, setHighlight] = useState(initialHighlight)
    return ( <HighlightContext.Provider value={highlight}>
                {props.children}
            </HighlightContext.Provider> 
    )}
