import React, { useState, useRef } from 'react';
import { createSelectionList, writers } from '../../Writers';
import './QuizForm.css';
import Turn from '../Turn/Turn';
import { useHistory } from 'react-router';

import './QuizForm.css';






export default function QuizForm() {

    const initialState = {
        writers,
        quizData: createSelectionList(writers),
    }
    const history = useHistory();

    const answer = useRef(false)




    const selectBookHandler = (title) => {
        answer.current = initialState.quizData.checkAnswer(title);
        if(initialState.answer) {
            console.log(initialState.answer)

            setTimeout(() => {

                history.push({ pathname: "/empty" });
                history.goBack();

            }, 1000);
             return initialState.answer === 'incorrect'


        }
        else {
            return;
        }

    }


    return (
        <div className="quiz_container">
        <header className="quiz_header">
            <h2>Author quiz</h2>
            <p> Select a right author</p>
        </header>
        <section style={{ backgroundColor: initialState.answer ? "correct" : "red" }}>
            <Turn quizData={initialState.quizData}   selectBookHandler={selectBookHandler} />

        </section>

    </div>


)

}
