import React from 'react';
import { writers, createSelectionList, selectCorrectAnswer } from '../../Writers';
import Turn from '../Turn/Turn';
import './QuizForm.css'


const QuizForm = () => {

    const initialState = {
         turnData: createSelectionList(writers),
         isCorrect: true
    }

    const selectBookHandler = () => {
        selectCorrectAnswer({
            author: initialState.turnData.author,
            answer: initialState.isCorrect
        }) 
        console.log('tere')       
    }

   return(
       <div className="quiz_container">
           <header className="quiz_header">
               <h2>Author quiz</h2>
               <p> Select a right author</p>
           </header>
           <section>
                <Turn selectBookHandler={selectBookHandler} books={initialState.turnData.books} author={initialState.turnData.author} />
           </section>
       </div>
   )

}

export default QuizForm;
