import React from 'react';
import Turn from '../Turn/Turn';
import { connect } from 'react-redux';
import './QuizForm.css'
import {initialState} from '../../App';
import Continue from '../continue/Continue';


const mapStateToProps = (state) => {
	return {
		turnData: state.turnData,
		answerStatus: state.answerStatus
	}
};
const mapDispatchToProps = (dispatch) => {
	return {
		onAnswerSelected: (answer) => dispatch({ type: 'ANSWER_SELECTED', answer }),
		onContinue: () => dispatch({ type: 'CONTINUE' })
	};
};


const QuizForm = connect(mapStateToProps, mapDispatchToProps)(({
	turnData,
	answerStatus,
	onAnswerSelected,
	onContinue
}) =>


       <div className="quiz_container">
           <header className="quiz_header">
               <h2>Author quiz</h2>
               <p> Select a right author</p>
           </header>
           <section>
                <Turn
                 {...turnData}
                 answerStatus={answerStatus}
                 books={initialState.turnData.books}
                 author={initialState.turnData.author}
                 onAnswerSelected={onAnswerSelected}
                 />

           </section>
           <Continue show={answerStatus === 'correct'} onContinue={onContinue}/>

       </div>


)

export default QuizForm;
