import React, { useState } from 'react';


export const HighlightContext = React.createContext({initialHighlight: {} })

export default props => {
    const [highlight, setHighlight] = useState({highlight: {
        'neutral': '',
        'correct': 'green',
        'incorrect': 'red'
    }})
    return(
        <HighlightContext.Provider value={{initialHighlight: highlight}}>
            {props.children}
        </HighlightContext.Provider>
    )
}
