import React from 'react';
import { writers } from '../../Writers';
import { createSelectionList } from '../../Writers';
import Turn from '../Turn/Turn';


const QuizForm = () => {

    const initialState = { turnData: createSelectionList(writers) }

   return(
       <div className="quiz_container">
           <header>
               <h2>Author quiz</h2>
               <p> Select a right author</p>
           </header>
           <Turn books={initialState.turnData.books} author={initialState.turnData.author} />
       </div>
   )

}

export default QuizForm;
