import React, { useContext } from 'react';
import { ColorContext } from '../../App';

const Book = React.memo(({title, selectBookHandler }) => {

  const colors = useContext(ColorContext);

  function highlightToBgColor(colors){
    const mapping = {
      'none':'',
      'correct': colors.blue,
      'wrong': colors.red
    };
    return mapping[colors];
  }

    return(  <div style={{ backgroundColor: colors.blue }} >
            <ul>
            <li  onClick={()=> {selectBookHandler(title)}}  className="book_list"> {title} </li>
        </ul>

        </div>

    )

})

export default Book;
