import {chain, sample} from 'underscore';


export const authors =[
    {
    name: 'Eno Raud',
    imageUrl:'https://tnp.ee/wp-content/uploads/2016/04/eno-raud-235x300.jpg',
    books: ['Sipsik', 'Naksitrallid']
    },
    {
    name: 'Jaan Kross',
    imageUrl:'https://kirjanduskeskus.ee/wp-content/uploads/2020/02/fc2535e27450857385-88712233.jpg',
    books: ['Kolme katku vahel', 'Tahtamaa']
    },
    {
    name: 'A. H. Tammsaare',
    imageUrl:'https://upload.wikimedia.org/wikipedia/commons/a/a4/Anton_Hansen_Tammsaare%2C_000290.jpg',
    imageSource:'Wikimedia Commons',
    imageAttribution:'Daniel Ogren',
    books: ['Tõde ja õigus', 'Kõrboja peremees']
    },
    {
    name: 'Fr. Tuglas',
    imageUrl:'https://upload.wikimedia.org/wikipedia/commons/3/32/Friedebert_Tuglas_1910.jpg',
    imageSource:'Wikimedia Commons',
    books: ['Väike Illimar', 'Meri']
    },
    {
    name: 'Oskar Luts',
    imageUrl:'http://admin.entsyklopeedia.ee/Portreed/Luts%20Oskar%20.jpg',
    imageSource:'Wikimedia Commons',
    books: ['Suvi', 'Kevade', 'Talve']
    }
]

function getTurnBooks(authors) {
	return chain(authors)
		.reduce((books, author) => books.concat(author.books), [])
		.shuffle()
		.slice(0, 4)
		.value();
}

export function getTurnData(authors) {
	const books = getTurnBooks(authors);
	const answer = sample(books);
	const author = authors.find((a) => a.books.includes(answer));

	return { books, author };
}
