import React from 'react';


const Book = React.memo(({title, selectBookHandler, highLight }) => {

    console.log(highLight)

    function getBackgroundColor(highlight){
        const mapping = {
          'none':'',
          'correct':'green',
          'incorrect':'red'
        };
        return mapping[highlight];
      }

    const backgroundColor = getBackgroundColor(highLight);

    return(
        <div >
            <ul>
            <li onClick={()=> {selectBookHandler(title)}} style={{backgroundColor: backgroundColor}} className="book_list"> {title} </li>
        </ul>

        </div>

    )

})

export default Book;
