import React, { useState, useRef } from 'react';
import { createSelectionList, writers } from '../../Writers';
import './QuizForm.css';
import Turn from '../Turn/Turn';
import { useHistory } from 'react-router';

import './QuizForm.css';






export default function QuizForm() {

    const initialState = {
        writers,
        quizData: createSelectionList(writers),
    }
    const history = useHistory();

    const answer = useRef('')




    const selectBookHandler = (title) => {
        answer.current = initialState.quizData.checkAnswer(title);
        if(answer.current) {


            setTimeout(() => {


                history.push({ pathname: "/empty" });
                history.goBack();

            }, 1000);
            return answer.current === 'corrent'



        }
        else {
            return answer.current === 'incorrect'
        }

    }

    console.log(answer.current)

    return (
        <div className="quiz_container">
        <header className="quiz_header">
            <h2>Author quiz</h2>
            <p> Select a right author</p>
        </header>
        <section style={{ backgroundColor: answer.current ? 'red' : 'blue' }}>
            <Turn quizData={initialState.quizData}   selectBookHandler={selectBookHandler} />

        </section>

    </div>


)

}
