import React, { useState } from 'react';
import { createSelectionList, writers } from '../../Writers';
import './QuizForm.css';
import Turn from '../Turn/Turn';
import { useHistory } from 'react-router';






export default function QuizForm() {

    const initialState = {
        writers,
        quizData: createSelectionList(writers),
    }
    const history = useHistory();


    const [answerStatus, setAnswerStatus] = useState('none');


    const selectBookHandler = (title) => {
        let isCorrect = initialState.quizData.checkAnswer(title);
        if(isCorrect) {
            setAnswerStatus('correct');
            history.push({ pathname: "/empty" });
           return history.goBack();

        }
        else {
            setAnswerStatus('incorrect');
            return;
        }

    }



    return (
        <div className="quiz_container">
        <header className="quiz_header">
            <h2>Author quiz</h2>
            <p> Select a right author</p>
        </header>
        <section>
            <Turn quizData={initialState.quizData} answerStatus={answerStatus}  selectBookHandler={selectBookHandler} />

        </section>

    </div>


)

}
