import React, { useMemo } from 'react';
import './App.css';
import QuizForm from './Quiz/QuizForm/QuizForm';

const colors = {
  blue: "#03619c",
  green: "#009900",
  red: "#9c0312"
};

export const ColorContext = React.createContext(null);


function App() {


const [highlight, setHighLight] = React.useState('none');

const value = useMemo(() => ( [ highlight, setHighLight ]), [highlight, setHighLight]);



     return (
       <div>
         <ColorContext.Provider value={value}>
           <QuizForm />
          </ColorContext.Provider>

      </div>
  );
}

export default App;
