import React, { useState } from 'react';

const initialHighlight = {
    neutral: "#000000",
    correct: " #009933",
    incorrect: "#ff3300"
}

export const HighlightContext = React.createContext(initialHighlight.correct)

export default props => {

    return(
        <HighlightContext.Provider value={initialHighlight}>
            {props.children}
        </HighlightContext.Provider>
    )
}
