import React, { useState, useRef } from 'react';
import { createSelectionList, writers } from '../../Writers';
import './QuizForm.css';
import Turn from '../Turn/Turn';
import { useHistory } from 'react-router';

import './QuizForm.css';






export default function QuizForm() {

    const initialState = {
        writers,
        quizData: createSelectionList(writers),
    }
    const history = useHistory();
    //const [answer , setAnswer] = useState({'none':'',
    //'correct':'green',
    //'wrong':'red'})

    const a = useRef('')

    const selectBookHandler = (title) => {
         let isCorrect = initialState.quizData.checkAnswer(title);
        if(isCorrect) {
            setTimeout(() => {  

                history.push({ pathname: "/empty" });
                history.goBack();
                return a.current == 'correct';
            }, 1000);   
        }
        else {
            return;
        }

    }
   console.log(a.current)

    return (

        <div className="quiz_container">
        <header className="quiz_header">
            <h2>Author quiz</h2>
            <p> Select a right author</p>
        </header>
        <section >
            <Turn quizData={initialState.quizData}   selectBookHandler={selectBookHandler} />

        </section>

    </div>


)

}
