import React from 'react';
import { createSelectionList, writers } from './Writers';
import {BrowserRouter as Router, Route} from 'react-router-dom'
import './App.css';
import QuizForm from './Quiz/QuizForm/QuizForm';

export const initialState = {
  writers,
  turnData: createSelectionList(writers),
  answerStatus: 'none'  
}

export const selectBookHandler = (bookId) => {
  let isCorrect = initialState.turnData.checkAnswer(bookId);
  return isCorrect ? 'correct' : 'incorrect'
}

function App() {

     return (
       <div>
         <Router>           
           <Route exact path="/" component={QuizForm} />
         </Router>  
      </div>
  );
}

export default App;
