import React from 'react';
import {Provider} from 'react-redux'
import { createSelectionList, writers } from './Writers';
import { createStore } from 'redux';
import {BrowserRouter as Router, Route} from 'react-router-dom'

import './App.css';
import QuizForm from './Quiz/QuizForm/QuizForm';

export const initialState = {
  writers,
  turnData: createSelectionList(writers),
  answerStatus: 'none'

}

const actionHandlers = {
	'ANSWER_SELECTED': (state, action) => Object.assign({}, state, { answerStatus: selectBookHandler(action.answer, state.turnData) }),
	'CONTINUE': (state, action) => Object.assign({}, state, { answerStatus: 'none', turnData: createSelectionList(state.authors) }),
};
const reducer = (state = initialState, action) => {
	let actionHandler = actionHandlers[action.type];

	return (actionHandler && actionHandler(state, action)) || state;
};

const store = createStore(
	reducer,
	window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
);


export const selectBookHandler = (answer) => {
  let isCorrect = initialState.turnData.checkAnswer(answer);
  return isCorrect ? 'correct' : 'incorrect'
}




function App() {



     return (
       <div>
         <Router>
         <Provider store={store}>

           <Route exact path="/" component={QuizForm} />
          </Provider>
         </Router>


      </div>
  );
}

export default App;
