import React from 'react';
import './Turn.css';
import Book from '../Book/Book';
import Typography from '@material-ui/core/Typography';




const Turn = ({quizData, selectBookHandler, answerStatus }) => {
    let progress = localStorage.getItem('progress');

    return(
        <React.Fragment>
        {progress && progress < 100 ? 

        <div className="quiz_content">
            <div className="authorImageDiv">
                <img alt="author_img" src={quizData.a.imageUrl}></img>
                <Typography variant="h5" component="h2">
                            {quizData.a.name}
                </Typography>
            </div>
             <div className="book_list_container">
                <ul>
                    {quizData && quizData.books.map(book => {
                        return <Book book={book}  onAnswerSelected={selectBookHandler} answerStatus={answerStatus}  /> 
                    })}
                </ul>
            </div>
        </div> : null }
        </React.Fragment>
    );

}

export default Turn;
