import React, { useState } from 'react';

const initialHighlight = {
    neutral: "#000000",
    correct: " #009933",
    incorrect: "#ff3300"
}

const HighlightContext = React.createContext(initialHighlight)

export default props => {
    return ( <HighlightContext.Provider value={initialHighlight}>
                {props.children}
            </HighlightContext.Provider> 
    )}
