import React, { useRef, useState, useEffect } from 'react';
import { createSelectionList, writers } from '../../Writers';
import Container from '@material-ui/core/Container';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import './QuizForm.css';
import Turn from '../Turn/Turn';
import { useHistory } from 'react-router';
import './QuizForm.css';
import ProgressBar from '../../UI/ProgressBar/ProgressBar';

export default function QuizForm() {

    const initialState = {
        writers,
        quizData: createSelectionList(writers),
    }
    const [progress, setProgress] = useState(0);
    const history = useHistory();
    const cardBackground = useRef(0);
    const isCorrect = useRef();

    const selectBookHandler = event => (title) => {
        event.preventDefault();
        isCorrect.current = initialState.quizData.checkAnswer(title);
    }

    useEffect(() => {

            cardBackground.current.style.backgroundColor = "green"
            setTimeout(() => {  
                setProgress((prevProgress) => (prevProgress >= 100 ? 10 : prevProgress + 10));
                history.push({ pathname: "/empty" });
                history.goBack();
            }, 1000);   



    }, [history]);



    useEffect(() => {
        const parsedCount = Number(localStorage.getItem("progress") || 0);
        setProgress(parsedCount)
      }, [])

    useEffect(() => {
        localStorage.setItem("progress", progress);
      }, [progress])

    return (
        <React.Fragment>     
        <Container maxWidth="md" className="quiz_container" >
            <ProgressBar progress={progress}  />
            <Card className="quiz_card" ref={cardBackground} style={{backgroundColor: "white" }}>
                <CardContent className="quiz_card_content">
                    <header className="quiz_header">
                        <Typography variant="h5" component="h2">
                            Book Quiz
                        </Typography>
                        <Typography variant="subtitle1" color="textSecondary">
                            Select a right book
                        </Typography>
                    </header>
                    <section className="quiz_section"         >
                        <Turn quizData={initialState.quizData}   selectBookHandler={selectBookHandler} />
                    </section>
                </CardContent>
            </Card>
        </Container>
        </React.Fragment>
    );
}
