import React from 'react';


const Book = ({onAnswerSelected, answerStatus, title  }) => {

  const getBackgroundColor = (answerStatus) => {
    return {
      none: 'white',
      correct: 'green',
      incorrect: 'red'
    }[answerStatus];
  }

  const backgroundColor = getBackgroundColor(answerStatus);



    return(  <div style={{ backgroundColor }}  >
            <ul>
            <li onClick={onAnswerSelected.bind(null, title)}> {title}  </li>
        </ul>

        </div>

    )

}

export default Book;
